import { useState, useEffect } from 'react';
import Player from './Player';


function Players() {

    //set theme to purple when rendering the players page
    useEffect(() => {
        setTheme('#38003c', 'purple');
    })

    const rawData = [
        {
            "name": "Bernd Leno",
            "price": 5.0,
            "total_points": 131,
            "minutes": 3131,
            "ppm": 0.04183966783775152,
            "ppp": 26.2,
            "ppppm": 0.008367933567550303
        },
        {
            "name": "Rúnar Alex Rúnarsson",
            "price": 4.0,
            "total_points": 1,
            "minutes": 15,
            "ppm": 0.06666666666666667,
            "ppp": 0.25,
            "ppppm": 0.016666666666666666
        },
        {
            "name": "Willian Borges Da Silva",
            "price": 6.5,
            "total_points": 78,
            "minutes": 1398,
            "ppm": 0.055793991416309016,
            "ppp": 12.0,
            "ppppm": 0.008583690987124463
        },
        {
            "name": "Pierre-Emerick Aubameyang",
            "price": 10.0,
            "total_points": 131,
            "minutes": 2330,
            "ppm": 0.056223175965665236,
            "ppp": 13.1,
            "ppppm": 0.0056223175965665236
        },
        {
            "name": "Cédric Soares",
            "price": 4.5,
            "total_points": 28,
            "minutes": 744,
            "ppm": 0.03763440860215054,
            "ppp": 6.222222222222222,
            "ppppm": 0.008363201911589008
        },
        {
            "name": "Alexandre Lacazette",
            "price": 8.5,
            "total_points": 129,
            "minutes": 1916,
            "ppm": 0.06732776617954071,
            "ppp": 15.176470588235293,
            "ppppm": 0.00792091366818126
        },
        {
            "name": "Bernd Leno",
            "price": 5.0,
            "total_points": 131,
            "minutes": 3131,
            "ppm": 0.04183966783775152,
            "ppp": 26.2,
            "ppppm": 0.008367933567550303
        },
        {
            "name": "Rúnar Alex Rúnarsson",
            "price": 4.0,
            "total_points": 1,
            "minutes": 15,
            "ppm": 0.06666666666666667,
            "ppp": 0.25,
            "ppppm": 0.016666666666666666
        },
        {
            "name": "Willian Borges Da Silva",
            "price": 6.5,
            "total_points": 78,
            "minutes": 1398,
            "ppm": 0.055793991416309016,
            "ppp": 12.0,
            "ppppm": 0.008583690987124463
        },
        {
            "name": "Pierre-Emerick Aubameyang",
            "price": 10.0,
            "total_points": 131,
            "minutes": 2330,
            "ppm": 0.056223175965665236,
            "ppp": 13.1,
            "ppppm": 0.0056223175965665236
        },
        {
            "name": "Cédric Soares",
            "price": 4.5,
            "total_points": 28,
            "minutes": 744,
            "ppm": 0.03763440860215054,
            "ppp": 6.222222222222222,
            "ppppm": 0.008363201911589008
        },
        {
            "name": "Alexandre Lacazette",
            "price": 8.5,
            "total_points": 129,
            "minutes": 1916,
            "ppm": 0.06732776617954071,
            "ppp": 15.176470588235293,
            "ppppm": 0.00792091366818126
        },
    ]
    
    const [data, setData] = useState(rawData);
    const [data2, setData2] = useState([]);

    // useEffect(() => {
    //     fetch('http://127.0.0.1:8000/api/app/')
    //     .then(res => res.json())
    //     .then(data => {
    //         setData(data);
    //         setData2(data);
    //     })
    //     .catch(err => {
    //         console.log(err);
    //     });
    // }, []); 

    return (
        <div id="players-main">
            <div id="search-bar-container">
                <input type="text" id="search-bar" placeholder="Search..." onKeyUp={() => {
                    let searchWord = document.getElementById('search-bar').value;
                    let newData = data2.filter(function(player) {
                        return player.name.includes(searchWord)
                    })
                    setData(newData)
                }}></input>
            </div>
            <div id="players-container">
                {data.map((player) => {
                    const { name, price, total_points, minutes, ppm, ppp, ppppm } = player;
                    return (
                        <Player name={name} price={price} total_points={total_points}
                        minutes={minutes} ppm={ppm} ppp={ppp} ppppm={ppppm} />
                    )
                })}
            </div>
        </div>
    )
}



/* Sets the theme of the page
* @param: primeColor - backgroundcolor of header and counter
* @param: secondaryColor - backgroundcolor of menu
*/
function setTheme(primeColor, secondaryColor) {
    const header = document.getElementById('header');
    header.style.backgroundColor = primeColor;

    const counter = document.getElementById('counter');
    counter.style.backgroundColor = primeColor;

    const menu = document.getElementById('menu');
    menu.style.backgroundColor = secondaryColor;
}


export default Players;
