import { useState } from 'react'
import { withStyles } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';
import Radio from '@material-ui/core/Radio';

function RadioButtonGroup(props) {

      //For the show name radio buttons
      const [selectedValue2, setSelectedValue2] = useState(props.value1);

      const handleChange2 = (event) => {
          setSelectedValue2(event.target.value);
          const newPref = props.getPrefFunction();
          props.prefChange(newPref);
      };
  
    return (
        <div className="radio-buttons">
            <div className="radio-button-group">
                <p>Always</p>
                    <GreenRadio 
                        checked={selectedValue2 === props.value1}
                        onChange={handleChange2}
                        value={props.value1}
                        name={props.name}
                    />
            </div>
            <div className="radio-button-group" id="radio-margin-left">
                <p>On hover</p>
                    <GreenRadio
                        checked={selectedValue2 === props.value2}
                        onChange={handleChange2}
                        value={props.value2}
                        name={props.name}
                    />
            </div>
        </div>
    )
}

const GreenRadio = withStyles({
    root: {
      color: green[400],
      '&$checked': {
        color: green[600],
      },
    },
    checked: {},
  })((props) => <Radio color="default" {...props} />);

  export default RadioButtonGroup;
