function getPreferencesChange() {
    const hAxisInput = document.getElementById('select-h-ax').value;
    const vAxisInput = document.getElementById('select-v-ax').value;

    const isCheckedGKP = document.getElementById('GKP-CB').checked;
    const isCheckedDEF = document.getElementById('DEF-CB').checked;
    const isCheckedMID = document.getElementById('MID-CB').checked;
    const isCheckedFWD = document.getElementById('FWD-CB').checked;

    const minutesPlayed = document.getElementById('minutes-slider').value;

    const maxPrice = document.getElementById('max-price').value;

    const selectTeam = document.getElementById('select-team').value;

    const radiosAvail = document.getElementsByName('radio-availability');
    let radioAvail;

    for (let i = 0; i < radiosAvail.length; i++) {
        if(radiosAvail[i].checked) {
            radioAvail = radiosAvail[i].value;
        }
    }

    const radiosShow = document.getElementsByName('radio-show');
    let radioShow;

    for (let i = 0; i < radiosShow.length; i++) {
        if(radiosShow[i].checked) {
            radioShow = radiosShow[i].value;
        }
    }

    return [hAxisInput, vAxisInput, isCheckedGKP, isCheckedDEF, isCheckedMID, isCheckedFWD, minutesPlayed, maxPrice, selectTeam, radioAvail, radioShow]

}

let optionsClicked = false;

function showOrHideMenu() {
    const dropDownMenu = document.getElementById('statsboard-options-container');
    const optionsButton = document.getElementById('options-button');

    if (!optionsClicked) {
        dropDownMenu.style.display = 'flex';
        optionsButton.innerHTML = 'Close';

    } 
    else {
        dropDownMenu.style.display = 'none';
        optionsButton.innerHTML = '+Options';
    }
    optionsClicked = !optionsClicked;
}

//IMPORTANT FUNCTION: filters and returns data according to user preferences
function getXYData(JSONdata, preferences) {
    let xDataGKP = []
    let yDataGKP = []
    let namesGKP = []

    let xDataDEF = []
    let yDataDEF = []
    let namesDEF = []

    let xDataMID = []
    let yDataMID = []
    let namesMID = []

    let xDataFWD = []
    let yDataFWD = []
    let namesFWD = []

    const xLabel = preferences[0]
    const yLabel = preferences[1]

    const GKPchecked = preferences[2]
    const DEFchecked = preferences[3]
    const MIDchecked = preferences[4]
    const FWDchecked = preferences[5]

    const  minMinutesPlayed = preferences[6]
    
    const maxPrice = preferences[7]

    const teamSelected = preferences[8]

    const playerAvailability = preferences[9]

    for (let i = 0; i < JSONdata.length; i++) {
        if ( (JSONdata[i]['minutes'] > minMinutesPlayed) && (JSONdata[i]['price'] < maxPrice || maxPrice === '') && (JSONdata[i]['team'] === teamSelected || teamSelected === 'All teams')
            && (JSONdata[i]['status'] === playerAvailability || playerAvailability === 'All')) {
            if (JSONdata[i]['position'] === 'GKP' && GKPchecked) {
                xDataGKP.push(JSONdata[i][xLabel])
                yDataGKP.push(JSONdata[i][yLabel])
                namesGKP.push(JSONdata[i]['web_name'])

            }
            if (JSONdata[i]['position'] === 'DEF' && DEFchecked) {
                xDataDEF.push(JSONdata[i][xLabel])
                yDataDEF.push(JSONdata[i][yLabel])
                namesDEF.push(JSONdata[i]['web_name'])

            }
            if (JSONdata[i]['position'] === 'MID' && MIDchecked) {
                xDataMID.push(JSONdata[i][xLabel])
                yDataMID.push(JSONdata[i][yLabel])
                namesMID.push(JSONdata[i]['web_name'])

            }
            if (JSONdata[i]['position'] === 'FWD' && FWDchecked) {
                xDataFWD.push(JSONdata[i][xLabel])
                yDataFWD.push(JSONdata[i][yLabel])
                namesFWD.push(JSONdata[i]['web_name'])

            }
            


        }
    }

    const GKPdata = [xDataGKP, yDataGKP, namesGKP]
    const DEFdata = [xDataDEF, yDataDEF, namesDEF]
    const MIDdata = [xDataMID, yDataMID, namesMID]
    const FWDdata = [xDataFWD, yDataFWD, namesFWD]

    const showNames = preferences[10]

    let text = ''

    if (showNames === 'Always') {
        text = '+text';
    }
    

    return [GKPdata, DEFdata, MIDdata, FWDdata, xLabel, yLabel, text];
}


