import { useEffect, useState } from "react";
import Selector from "../statpage/Selector";

function RecommenderInput( props ) {

    const sendDataToParent = (prefIndex, newValue) => {
        let newPref = [...preferences]
        newPref[prefIndex] = newValue;
        setPreferences(newPref);
    }

    return (
        <div id="recommender-input-container">
            <h3>Input</h3>
            <div id="recommender-input-bank">
                <p>Total amount in bank after all sales are done</p>
                <input type="number" className="rec-number-input"></input>
            </div>
            <div id="recommender-input-positions">
                <p>Number of players in each position needing replacement</p>
                <div id="rec-position-selection">
                    <label for="rec-gkp">GKP</label>
                    <input type="number" className="rec-number-input" name="rec-gkp"></input>
                    <label for="rec-gkp">GKP</label>
                    <input type="number" className="rec-number-input" name="rec-gkp"></input>
                    <label for="rec-gkp">GKP</label>
                    <input type="number" className="rec-number-input" name="rec-gkp"></input>
                    <label for="rec-gkp">GKP</label>
                    <input type="number" className="rec-number-input" name="rec-gkp"></input>
                </div>
            </div>
            <div id="recommender-input-teams">
                <p>Teams not to include</p>
                <Selector 
                    sendDataToParent={sendDataToParent}
                    prefIndex={8}
                    values={['All teams', 'Arsenal', 'Aston Villa', 'Brentford', 'Brighton', 'Burnley', 'Chelsea', 'Crystal Palace', 'Everton', 'Leeds',
                            'Leicester', 'Liverpool', 'Man City', 'Man Utd', 'Newcastle', 'Norwich', 'Southampton', 'Spurs', 'Watford', 'West Ham', 'Wolves']}
                    initialState={'All teams'}
                />
            </div>
        </div>
    )
}

export default RecommenderInput;
