import Plotly from "plotly.js";
import createPlotlyComponent from 'react-plotly.js/factory';
import { useState, useEffect } from 'react';
import setTheme from "../../utils/Themes";
import OptionsContainer from "./OptionsContainer";

const Plot = createPlotlyComponent(Plotly);

function Statsboard() {

    const rawData = [
        {
            "name": "Bernd Leno",
            "price": 5.0,
            "total_points": 131,
            "minutes": 3131,
            "ppm": 0.04183966783775152,
            "ppp": 26.2,
            "ppppm": 0.008367933567550303
        },
        {
            "name": "Rúnar Alex Rúnarsson",
            "price": 4.0,
            "total_points": 1,
            "minutes": 15,
            "ppm": 0.06666666666666667,
            "ppp": 0.25,
            "ppppm": 0.016666666666666666
        },
        {
            "name": "Willian Borges Da Silva",
            "price": 6.5,
            "total_points": 78,
            "minutes": 1398,
            "ppm": 0.055793991416309016,
            "ppp": 12.0,
            "ppppm": 0.008583690987124463
        },
        {
            "name": "Pierre-Emerick Aubameyang",
            "price": 10.0,
            "total_points": 131,
            "minutes": 2330,
            "ppm": 0.056223175965665236,
            "ppp": 13.1,
            "ppppm": 0.0056223175965665236
        },
        {
            "name": "Cédric Soares",
            "price": 4.5,
            "total_points": 28,
            "minutes": 744,
            "ppm": 0.03763440860215054,
            "ppp": 6.222222222222222,
            "ppppm": 0.008363201911589008
        },
        {
            "name": "Alexandre Lacazette",
            "price": 8.5,
            "total_points": 129,
            "minutes": 1916,
            "ppm": 0.06732776617954071,
            "ppp": 15.176470588235293,
            "ppppm": 0.00792091366818126
        },
        {
            "name": "Bernd Leno",
            "price": 5.0,
            "total_points": 131,
            "minutes": 3131,
            "ppm": 0.04183966783775152,
            "ppp": 26.2,
            "ppppm": 0.008367933567550303
        },
        {
            "name": "Rúnar Alex Rúnarsson",
            "price": 4.0,
            "total_points": 1,
            "minutes": 15,
            "ppm": 0.06666666666666667,
            "ppp": 0.25,
            "ppppm": 0.016666666666666666
        },
        {
            "name": "Willian Borges Da Silva",
            "price": 6.5,
            "total_points": 78,
            "minutes": 1398,
            "ppm": 0.055793991416309016,
            "ppp": 12.0,
            "ppppm": 0.008583690987124463
        },
        {
            "name": "Pierre-Emerick Aubameyang",
            "price": 10.0,
            "total_points": 131,
            "minutes": 2330,
            "ppm": 0.056223175965665236,
            "ppp": 13.1,
            "ppppm": 0.0056223175965665236
        },
        {
            "name": "Cédric Soares",
            "price": 4.5,
            "total_points": 28,
            "minutes": 744,
            "ppm": 0.03763440860215054,
            "ppp": 6.222222222222222,
            "ppppm": 0.008363201911589008
        },
        {
            "name": "Alexandre Lacazette",
            "price": 8.5,
            "total_points": 129,
            "minutes": 1916,
            "ppm": 0.06732776617954071,
            "ppp": 15.176470588235293,
            "ppppm": 0.00792091366818126
        },
    ]

    const [ data, setData ] = useState(rawData);

    // useEffect(() => {
    //     fetch('http://127.0.0.1:8000/api/app/')
    //     .then(res => res.json())
    //     .then(data => {
    //         setData(data);
    //     })
    //     .catch(err => {
    //         console.log(err);
    //     });
    // }, []); 

    //Set theme to black when rendering the Statboard
    useEffect(() => {
        setTheme('black', 'grey');
    });

    const [ preferences, setPreferences ] = useState([]); 
    
    useEffect(() => {
        setPreferences(getPreferencesChange());
    }, []);
 

    const [ xData, yData, names, xLabel, yLabel] = getXYData(data, preferences)

    return (
        <div id="statsboard">
            <div id="statsboard-options-title" >
                <p id="options-button" onClick={showOrHideMenu}>+Options</p>
            </div>
            <OptionsContainer prefChange={(pref) => setPreferences(pref)}/>
            <div id="plot-container">
                <Plot 
                    data={[
                        {
                            x: xData,
                            y: yData,
                            type: 'scatter',
                            text: names,
                            mode: 'markers+text',
                            textposition: 'top center',
                            marker: { size: 8 },
                        },
                    ]}
                    layout={ {
                        height: 480,
                        xaxis: {
                            title: hAxisPref,
                            autorange: true,
                            gridcolor: 'grey',
                            linecolor: '#636363',
                            linewidth: 2
                        },
                        yaxis: {
                            title: vAxisPref,
                            autorange: true,
                            gridcolor: 'grey',
                            linecolor: '#636363',
                            linewidth: 2
                        },
                        hovermode: 'closest',
                        plot_bgcolor:"#222",
                        paper_bgcolor:"#222",
                        font: {
                            color: "white",
                        }
                    }}
                    config = {{responsive: true}}
                />
            </div>
        </div>
    )
};

function getPreferencesChange() {
    const hAxisInput = document.getElementById('select-h-ax').value;
    const vAxisInput = document.getElementById('select-v-ax').value;

    const isCheckedGKP = document.getElementById('GKP-CB').checked;
    const isCheckedDEF = document.getElementById('DEF-CB').checked;
    const isCheckedMID = document.getElementById('MID-CB').checked;
    const isCheckedFWD = document.getElementById('FWD-CB').checked;

    const minutesPlayed = document.getElementById('minutes-slider').value;

    const maxPrice = document.getElementById('max-price').value;

    const selectTeam = document.getElementById('select-team').value;

    return [hAxisInput, vAxisInput, isCheckedGKP, isCheckedDEF, isCheckedMID, isCheckedFWD]

}

let optionsClicked = false;

function showOrHideMenu() {
    const dropDownMenu = document.getElementById('statsboard-options-container');
    const optionsButton = document.getElementById('options-button');

    if (!optionsClicked) {
        dropDownMenu.style.display = 'flex';
        optionsButton.innerHTML = 'Close';

    } 
    else {
        dropDownMenu.style.display = 'none';
        optionsButton.innerHTML = '+Options';
    }
    optionsClicked = !optionsClicked;
}

function getXYData(JSONdata, preferences) {
    let xData = []
    let yData = []
    let names = []

    const xLabel = preferences[0]
    const yLabel = preferences[1]

    for (let i = 0; i < JSONdata.length; i++) {
        if (JSONdata[i]['minutes'] > 1000) {
            xData.push(JSONdata[i][xLabel])
            yData.push(JSONdata[i][yLabel])
            names.push(JSONdata[i]['name'])
        }
    }

    return [xData, yData, names, xLabel, yLabel];
}


export default Statsboard;
