                            mode: 'markers+text',
                            textposition: 'top center',
                            marker: { size: 8 },
                        },
                    ]}
                    layout={ {
                        xaxis: {
                            title: hAxisPref,
                            autorange: true,
                            gridcolor: 'grey',
                            linecolor: '#636363',
                            linewidth: 2
                        },
                        yaxis: {
                            title: vAxisPref,
                            autorange: true,
                            gridcolor: 'grey',
                            linecolor: '#636363',
                            linewidth: 2
                        },
                        hovermode: 'closest',
                        plot_bgcolor:"#222",
                        paper_bgcolor:"#222",
                        font: {
                            color: "white",
                        }
                    }}
                    config = {{responsive: true}}
                />
            </div>
        </div>
    )
};

