import Counter from './Counter';
import recImg from '../images/grass-2616911.jpg';
import capImg from '../images/Captain.jpg';
import statImg from '../images/stats2.PNG';

const options1 = [
    {
        optionName: "Recommender",
        description: "Will recommend you a transfer",
        backgroundImg: recImg
    },
    {
        optionName: "Plot builder",
        description: "Will show you stats",
        backgroundImg: statImg
    }
    
]

const options2 = [
    {
        optionName: "Captain Picks",
        description: "Helps you select captain for GW1",
        backgroundImg: capImg

    },
    {
        optionName: "Players",
        description: "An overview of all the players registered in FPL",
        backgroundImg: ""
    }
]

function Home() {
    return (
        <div id="home-main">
            <div className="home-options-row">
                {options1.map((option) => {
                    const { optionName, description, backgroundImg } = option;
                    return (
                        <HomeOption optionName={optionName} description={description} backgroundImg={backgroundImg}/>
                    )
                })}
            </div>
            <div className="home-options-row">
                {options2.map((option) => {
                    const { optionName, description, backgroundImg } = option;
                    return (
                        <HomeOption optionName={optionName} description={description} backgroundImg={backgroundImg}/>
                    )
                })}
            </div>
        </div>
    )
};

function HomeOption(props) {
    return (
        <div className="home-option-container" style={{backgroundImage: `url(${props.backgroundImg})`}}>
            <h2>{props.optionName}</h2>
            <p>{props.description}</p>
        </div>
    )
}

export default Home;
