import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import RadioButtonGroup from './RadioButtonGroup';

function OptionsContainer( props ) {

    // //For the checkboxes
    // const [state, setState] = useState({
    //     checkedA: true,
    //     checkedB: true,
    //     checkedF: true,
    //     checkedG: true,
    //   });

    // const handleChange3 = (event) => {
    //     setState({ ...state, [event.target.name]: event.target.checked });
    //     const newPref = getPreferencesChange();
    //     props.prefChange(newPref);
    // };

    // //For the availability radio buttons
    // const [selectedValue, setSelectedValue] = useState('Available');

    // const handleChange = (event) => {
    //     setSelectedValue(event.target.value);
    //     const newPref = getPreferencesChange();
    //     props.prefChange(newPref);
    // };

    return (
        <div id="statsboard-options-container">
                {/* X-AXIS OPTIONS */}
                <div className="stats-option-box">
                    <h4>Horisontal axis</h4>
                    <select className="stats-select" id="select-h-ax" onChange={() => {
                        const newPref = getPreferencesChange();
                        props.prefChange(newPref);
                    }}>
                        <option value="price">Price</option>
                        <option value="total_points">Total points</option>
                        <option value="minutes">Minutes played</option>
                    </select>
                </div>
                {/* Y-AXIS OPTIONS */}
                <div className="stats-option-box">
                    <h4>Vertical axis</h4>
                    <select className="stats-select" id="select-v-ax" onChange={() => {
                        const newPref = getPreferencesChange();
                        props.prefChange(newPref);
                    }}>
                        <option value="points_per_minute">Points/Minutes</option>
                        <option value="points_per_price">Points/Price</option>
                        <option value="points_per_price_per_minute">Points/Price/Minutes</option>
                    </select>
                </div>
                {/* POSITIONS OPTIONS */}
                <div className="stats-option-box" id="statsboard-options-positions">
                        <div id="stats-checkbox-container">
                            {/* <label htmlFor="GKP">GKP</label>
                            <input type="checkbox" name="GKP" id="GKP-CB" defaultChecked="checked" onChange={() => {
                                const newPref = getPreferencesChange();
                                props.prefChange(newPref);
                             }} 
                            ></input> */}
                            {/* <FormControlLabel
                                control={<GreenCheckbox checked={state.checkedA} onChange={handleChange3} name="checkedA" id="GKP-CB" />}
                                label="GKP"
                                labelPlacement="top"
                            />
                            <FormControlLabel
                                control={<GreenCheckbox checked={state.checkedB} onChange={handleChange3} name="checkedB" id="DEF-CB" />}
                                label="DEF"
                                labelPlacement="top"
                            />
                            <FormControlLabel
                                control={<GreenCheckbox checked={state.checkedF} onChange={handleChange3} name="checkedF" id="MID-CB" />}
                                label="MID"
                                labelPlacement="top"
                            />
                            <FormControlLabel
                                control={<GreenCheckbox checked={state.checkedG} onChange={handleChange3} name="checkedG" id="FWD-CB" />}
                                label="FWD"
                                labelPlacement="top"
                            /> */}
                        </div>
                </div>
                {/* MINUTES OPTIONS */}
                <div className="stats-option-box" id="statsboard-options-minutes">
                   <h4>Min. minutes played</h4>
                   <div id="minutes-container">
                       <input type="range" id="minutes-slider" onInput={slideChange} min="0" max="2000" defaultValue="1000" onChange={() => {
                                const newPref = getPreferencesChange();
                                props.prefChange(newPref);
                             }} 
                        ></input>
                       <p id="minutes-display">1000</p>
                   </div>
                </div>
                {/* MAX PRICE */}
                <div className="stats-option-box" id="statsboard-options-minutes" >
                    <h4>Max. price</h4>
                    <input type="number" id="max-price" min="4" max="14.0" step="0.1" defaultValue="14.0" onChange={() => {
                                const newPref = getPreferencesChange();
                                props.prefChange(newPref);
                             }} 
                    ></input>
                </div>
                {/* TEAMS OPTIONS */}
                <div className="stats-option-box" id="statsboard-options-teams">
                    <h4>Select team</h4>
                    <select className="stats-select" id="select-team" onChange={() => {
                            const newPref = getPreferencesChange();
                            props.prefChange(newPref);
                    }}>
                        <option value="All teams">All teams</option>
                        <option value="Arsenal">Arsenal</option>
                        <option value="Aston Villa">Aston Villa</option>
                        <option value="Brentford">Brentford</option>
                        <option value="Brighton">Brighton</option>
                        <option value="Burnley">Burnley</option>
                        <option value="Chelsea">Chelsea</option>
                        <option value="Crystal Palace">Crystal Palace</option>
                        <option value="Everton">Everton</option>
                        <option value="Leeds">Leeds</option>
                        <option value="Leicester">Leicester</option>
                        <option value="Liverpool">Liverpool</option>
                        <option value="Man City">Man City</option>
                        <option value="Man United">Man United</option>
                        <option value="Newcastle">Newcastle</option>
                        <option value="Norwich">Norwich</option>
                        <option value="Southampton">Southampton</option>
                        <option value="Spurs">Spurs</option>
                        <option value="Watford">Watford</option>
                        <option value="West Ham">West Ham</option>
                        <option value="Wolves">Wolves</option>
                    </select>
                </div>
                {/* AVAILABILITY */}
                <div className="stats-option-box" id="statsboard-options-teams">
                    <h4>Player availability</h4>
                    {/* <div className="radio-buttons">
                        <div className="radio-button-group">
                            <p>Available</p>
                            <GreenRadio 
                                checked={selectedValue === 'Available'}
                                onChange={handleChange}
                                value="Available"
                                name="radio-availability"
                            />
                        </div>
                        <div className="radio-button-group" id="radio-margin-left">
                            <p>All</p>
                            <GreenRadio 
                                checked={selectedValue === 'All'}
                                onChange={handleChange}
                                value="All"
                                name="radio-availability"
                            />
                        </div>
                    </div> */}
                </div> 
                {/* SHOW NAMES */}
                <div className="stats-option-box" id="statsboard-options-teams">
                    <h4>Show names</h4>
                    <RadioButtonGroup />
                </div>  
            </div>
    )
}

const GreenCheckbox = withStyles({
    root: {
      color: green[400],
      '&$checked': {
        color: green[600],
      },
    },
    checked: {},
  })((props) => <Checkbox color="default" {...props} />);


  function getPreferencesChange() {
    const hAxisInput = document.getElementById('select-h-ax').value;
    const vAxisInput = document.getElementById('select-v-ax').value;

    const isCheckedGKP = document.getElementById('GKP-CB').checked;
    const isCheckedDEF = document.getElementById('DEF-CB').checked;
    const isCheckedMID = document.getElementById('MID-CB').checked;
    const isCheckedFWD = document.getElementById('FWD-CB').checked;

    const minutesPlayed = document.getElementById('minutes-slider').value;

    const maxPrice = document.getElementById('max-price').value;
   

    const selectTeam = document.getElementById('select-team').value;

    const radiosAvail = document.getElementsByName('radio-availability');
    let radioAvail;

    for (let i = 0; i < radiosAvail.length; i++) {
        if(radiosAvail[i].checked) {
            radioAvail = radiosAvail[i].value;
        }
    }

    const radiosShow = document.getElementsByName('radio-show');
    let radioShow;

    for (let i = 0; i < radiosShow.length; i++) {
        if(radiosShow[i].checked) {
            radioShow = radiosShow[i].value;
        }
    }

    return [hAxisInput, vAxisInput, isCheckedGKP, isCheckedDEF, isCheckedMID, isCheckedFWD, minutesPlayed, maxPrice, selectTeam, radioAvail, radioShow]

}

function slideChange() {
    var slider = document.getElementById("minutes-slider");
    var output = document.getElementById("minutes-display");
    output.innerHTML = slider.value; // Display the default slider value

    // Update the current slider value (each time you drag the slider handle)
    slider.oninput = function() {
    output.innerHTML = this.value;
    }
}

export default OptionsContainer;
