import Plotly from "plotly.js";
import createPlotlyComponent from 'react-plotly.js/factory';
import { useState, useEffect } from 'react';
import dropDownArrow from '../../static/img/drop-down-arrow.png';

const Plot = createPlotlyComponent(Plotly);

function Statsboard() {

    const rawData = [
        {
            "name": "Bernd Leno",
            "price": 5.0,
            "total_points": 131,
            "minutes": 3131,
            "ppm": 0.04183966783775152,
            "ppp": 26.2,
            "ppppm": 0.008367933567550303
        },
        {
            "name": "Rúnar Alex Rúnarsson",
            "price": 4.0,
            "total_points": 1,
            "minutes": 15,
            "ppm": 0.06666666666666667,
            "ppp": 0.25,
            "ppppm": 0.016666666666666666
        },
        {
            "name": "Willian Borges Da Silva",
            "price": 6.5,
            "total_points": 78,
            "minutes": 1398,
            "ppm": 0.055793991416309016,
            "ppp": 12.0,
            "ppppm": 0.008583690987124463
        },
        {
            "name": "Pierre-Emerick Aubameyang",
            "price": 10.0,
            "total_points": 131,
            "minutes": 2330,
            "ppm": 0.056223175965665236,
            "ppp": 13.1,
            "ppppm": 0.0056223175965665236
        },
        {
            "name": "Cédric Soares",
            "price": 4.5,
            "total_points": 28,
            "minutes": 744,
            "ppm": 0.03763440860215054,
            "ppp": 6.222222222222222,
            "ppppm": 0.008363201911589008
        },
        {
            "name": "Alexandre Lacazette",
            "price": 8.5,
            "total_points": 129,
            "minutes": 1916,
            "ppm": 0.06732776617954071,
            "ppp": 15.176470588235293,
            "ppppm": 0.00792091366818126
        },
        {
            "name": "Bernd Leno",
            "price": 5.0,
            "total_points": 131,
            "minutes": 3131,
            "ppm": 0.04183966783775152,
            "ppp": 26.2,
            "ppppm": 0.008367933567550303
        },
        {
            "name": "Rúnar Alex Rúnarsson",
            "price": 4.0,
            "total_points": 1,
            "minutes": 15,
            "ppm": 0.06666666666666667,
            "ppp": 0.25,
            "ppppm": 0.016666666666666666
        },
        {
            "name": "Willian Borges Da Silva",
            "price": 6.5,
            "total_points": 78,
            "minutes": 1398,
            "ppm": 0.055793991416309016,
            "ppp": 12.0,
            "ppppm": 0.008583690987124463
        },
        {
            "name": "Pierre-Emerick Aubameyang",
            "price": 10.0,
            "total_points": 131,
            "minutes": 2330,
            "ppm": 0.056223175965665236,
            "ppp": 13.1,
            "ppppm": 0.0056223175965665236
        },
        {
            "name": "Cédric Soares",
            "price": 4.5,
            "total_points": 28,
            "minutes": 744,
            "ppm": 0.03763440860215054,
            "ppp": 6.222222222222222,
            "ppppm": 0.008363201911589008
        },
        {
            "name": "Alexandre Lacazette",
            "price": 8.5,
            "total_points": 129,
            "minutes": 1916,
            "ppm": 0.06732776617954071,
            "ppp": 15.176470588235293,
            "ppppm": 0.00792091366818126
        },
    ]

    const [ data, setData ] = useState(rawData);

    const XYNamesData = getXYData(data, 'price', 'ppm')

    return (
        <div id="statsboard">
            <div id="statsboard-options-title"  onClick={showOrHideMenu}>
                <p>Options</p>
                <img id="drop-down-arrow" src={dropDownArrow} alt="drop down arrow"></img>
            </div>
            <div id="statsboard-options-container">
                <div class="stats-option-box" id="statsboard-options-xaxis">
                    <h4>Axis</h4>
                    <select>
                        <option></option>
                    </select>
                    <select>
                        <option></option>
                    </select>
                </div>
                <div class="stats-option-box" id="statsboard-options-positions">
                    <h4>Positions</h4>
                </div>
                <div class="stats-option-box" id="statsboard-options-minutes">
                   <h4>Minutes</h4>
                </div>
                <div class="stats-option-box" id="statsboard-options-teams">
                    <h4>Teams</h4>
                </div>
                <div class="stats-option-box" id="statsboard-options-show-names">
                    <h4>Show names</h4>
                </div>
            </div>
            <div id="plot-container">
                <Plot 
                    data={[
                        {
                            x: XYNamesData[0],
                            y: XYNamesData[1],
                            type: 'scatter',
                            text: XYNamesData[2],
                            mode: 'markers+text',
                            textposition: 'top center',
                            marker: { size: 8 },
                        },
                    ]}
                    layout={ 
                        { title: 'Your Plot'} }
                    config = {{responsive: true}}
                />
            </div>
        </div>
    )
};

let optionsClicked = false;

function showOrHideMenu() {
    const dropDownMenu = document.getElementById('statsboard-options-container');
    const dropDownArrow = document.getElementById('drop-down-arrow');

    if (!optionsClicked) {
        dropDownMenu.style.display = 'flex';
        dropDownArrow.style.transform = 'rotate(-180deg)';

    } 
    else {
        dropDownMenu.style.display = 'none';
        dropDownArrow.style.transform = 'rotate(0)';
    }
    optionsClicked = !optionsClicked;
}

function getXYData(JSONdata, xLabel, yLabel) {
    let xData = []
    let yData = []
    let names = []
    for (let i = 0; i < JSONdata.length; i++) {
        xData.push(JSONdata[i][xLabel])
        yData.push(JSONdata[i][yLabel])
        names.push(JSONdata[i]['name'])
    }

    return [xData, yData, names];
}

export default Statsboard;
