import Plotly from "plotly.js";
import createPlotlyComponent from 'react-plotly.js/factory';
import { useState, useEffect } from 'react';

const Plot = createPlotlyComponent(Plotly);

function Statsboard() {

    const rawData = [
        {
            "name": "Bernd Leno",
            "price": 5.0,
            "total_points": 131,
            "minutes": 3131,
            "ppm": 0.04183966783775152,
            "ppp": 26.2,
            "ppppm": 0.008367933567550303
        },
        {
            "name": "Rúnar Alex Rúnarsson",
            "price": 4.0,
            "total_points": 1,
            "minutes": 15,
            "ppm": 0.06666666666666667,
            "ppp": 0.25,
            "ppppm": 0.016666666666666666
        },
        {
            "name": "Willian Borges Da Silva",
            "price": 6.5,
            "total_points": 78,
            "minutes": 1398,
            "ppm": 0.055793991416309016,
            "ppp": 12.0,
            "ppppm": 0.008583690987124463
        },
        {
            "name": "Pierre-Emerick Aubameyang",
            "price": 10.0,
            "total_points": 131,
            "minutes": 2330,
            "ppm": 0.056223175965665236,
            "ppp": 13.1,
            "ppppm": 0.0056223175965665236
        },
        {
            "name": "Cédric Soares",
            "price": 4.5,
            "total_points": 28,
            "minutes": 744,
            "ppm": 0.03763440860215054,
            "ppp": 6.222222222222222,
            "ppppm": 0.008363201911589008
        },
        {
            "name": "Alexandre Lacazette",
            "price": 8.5,
            "total_points": 129,
            "minutes": 1916,
            "ppm": 0.06732776617954071,
            "ppp": 15.176470588235293,
            "ppppm": 0.00792091366818126
        },
        {
            "name": "Bernd Leno",
            "price": 5.0,
            "total_points": 131,
            "minutes": 3131,
            "ppm": 0.04183966783775152,
            "ppp": 26.2,
            "ppppm": 0.008367933567550303
        },
        {
            "name": "Rúnar Alex Rúnarsson",
            "price": 4.0,
            "total_points": 1,
            "minutes": 15,
            "ppm": 0.06666666666666667,
            "ppp": 0.25,
            "ppppm": 0.016666666666666666
        },
        {
            "name": "Willian Borges Da Silva",
            "price": 6.5,
            "total_points": 78,
            "minutes": 1398,
            "ppm": 0.055793991416309016,
            "ppp": 12.0,
            "ppppm": 0.008583690987124463
        },
        {
            "name": "Pierre-Emerick Aubameyang",
            "price": 10.0,
            "total_points": 131,
            "minutes": 2330,
            "ppm": 0.056223175965665236,
            "ppp": 13.1,
            "ppppm": 0.0056223175965665236
        },
        {
            "name": "Cédric Soares",
            "price": 4.5,
            "total_points": 28,
            "minutes": 744,
            "ppm": 0.03763440860215054,
            "ppp": 6.222222222222222,
            "ppppm": 0.008363201911589008
        },
        {
            "name": "Alexandre Lacazette",
            "price": 8.5,
            "total_points": 129,
            "minutes": 1916,
            "ppm": 0.06732776617954071,
            "ppp": 15.176470588235293,
            "ppppm": 0.00792091366818126
        },
    ]

    const [ data, setData ] = useState(rawData);

    // useEffect(() => {
    //     fetch('http://127.0.0.1:8000/api/app/')
    //     .then(res => res.json())
    //     .then(data => {
    //         setData(data);
    //     })
    //     .catch(err => {
    //         console.log(err);
    //     });
    // }, []); 

    const XYNamesData = getXYData(data, 'price', 'ppm')

    return (
        <div id="statsboard">
            <div id="statsboard-options-title" >
                <p id="options-button" onClick={showOrHideMenu}>+Options</p>
            </div>
            <OptionsContainer/>
            <div id="plot-container">
                <Plot 
                    data={[
                        {
                            x: XYNamesData[0],
                            y: XYNamesData[1],
                            type: 'scatter',
                            text: XYNamesData[2],
                            mode: 'markers+text',
                            textposition: 'top center',
                            marker: { size: 8 },
                        },
                    ]}
                    layout={ {
                        xaxis: {
                            autorange: true,
                        },
                        yaxis: {
                            autorange: true,
                        },
                        hovermode: 'closest',
                        title: 'Your Plot',
                        plot_bgcolor:"black",
                        paper_bgcolor:"#black",
                    }}
                    config = {{responsive: true}}
                />
            </div>
        </div>
    )
};

function OptionsContainer() {
    return (
        <div id="statsboard-options-container">
                {/* X-AXIS OPTIONS */}
                <div class="stats-option-box" id="statsboard-options-xaxis">
                    <h4>Horisontal axis</h4>
                    <select class="stats-select">
                        <option>Price</option>
                        <option>Total points</option>
                        <option>Minutes played</option>
                    </select>
                </div>
                {/* Y-AXIS OPTIONS */}
                <div class="stats-option-box" id="statsboard-options-xaxis">
                    <h4>Vertical axis</h4>
                    <select class="stats-select">
                        <option>Points/Minutes</option>
                        <option>Points/Price</option>
                        <option>Points/Price/Minutes</option>
                    </select>
                </div>
                {/* POSITIONS OPTIONS */}
                <div class="stats-option-box" id="statsboard-options-positions">
                    <h4>Select positions</h4>
                        <div id="stats-checkbox-container">
                            <label for="GKP">GKP</label>
                            <input type="checkbox" name="GKP" defaultChecked="checked"></input>
                            <label for="DEF">DEF</label>
                            <input type="checkbox" name="DEF" defaultChecked="checked"></input>
                            <label for="MID">MID</label>
                            <input type="checkbox" name="MID" defaultChecked="checked"></input>
                            <label for="FWD">FWD</label>
                            <input type="checkbox" name="FWD" defaultChecked="checked"></input>
                        </div>
                </div>
                {/* MINUTES OPTIONS */}
                <div class="stats-option-box" id="statsboard-options-minutes">
                   <h4>Min. minutes played</h4>
                   <div id="minutes-container">
                       <input type="range" id="minutes-slider"></input>
                       <p>1000</p>
                   </div>
                </div>
                {/* MAX PRICE */}
                <div class="stats-option-box" id="statsboard-options-xaxis">
                    <h4>Max. price</h4>
                    <input type="number"></input>
                </div>
                {/* TEAMS OPTIONS */}
                <div class="stats-option-box" id="statsboard-options-teams">
                    <h4>Select team</h4>
                    <select class="stats-select">
                        <option>Arsenal</option>
                        <option>Aston Villa</option>
                        <option>Chelsea</option>
                        <option>Manchester United</option>
                        <option>Manchester City</option>
                    </select>
                </div>
                {/* SHOW NAMES */}
                <div class="stats-option-box" id="statsboard-options-teams">
                    <h4>Show names</h4>
                </div>
                {/* SOMETHING ELSE */}
