import { useEffect, useState } from 'react';
import setTheme from '../../utils/Themes';
import General from './sub-components/General';
import Customized from './sub-components/Customized';

function CaptainPicks( props ) {

    //set theme to purple when rendering the players page
    useEffect(() => {
        setTheme('#38003c', '#530553');
    }, [])

    useEffect(() => {
        props.handlePageSwitch('Captain Picks');
    }, [])

    const [ selected, setSelected ] = useState('GENERAL')

    return (
        <div id="captain-picks-main">
            <TabContainer 
                setSelected={(newSelection) => { setSelected(newSelection)}}
                selected={selected}
            />
            {selected === 'GENERAL' && <General />}
            {selected === 'CUSTOM' && <Customized />}
        </div>
    )
}

function TabContainer( props ) {

    const generalColor = () => {
    }
    return (
        <div id="captain-picks-tab-container">
            <Tab text={'GENERAL'} setSelected={props.setSelected} color={() => { return props.selected === 'GENERAL' ? 'white' : '#888888'}}/> 
            <Tab text={'CUSTOM'} setSelected={props.setSelected} color={() => { return props.selected === 'CUSTOM' ? 'white' : '#888888'}}/> 
        </div>
    )
}

function Tab( props ) {

    const handleClick = () => {
        props.setSelected(props.text)
    }

    return (
        <div className="captain-picks-tab" onClick={handleClick}>
            <h4>{props.text} <span>CAPTAIN PICKS</span></h4>
        </div>
    )
}

export default CaptainPicks;
