function OptionsContainer( props ) {
    return (
        <div id="statsboard-options-container">
                {/* X-AXIS OPTIONS */}
                <div className="stats-option-box">
                    <h4>Horisontal axis</h4>
                    <select className="stats-select" id="select-h-ax" onChange={() => {
                        const newPref = getPreferencesChange();
                        props.prefChange(newPref);
                    }}>
                        <option value="price">Price</option>
                        <option value="total_points">Total points</option>
                        <option value="minutes">Minutes played</option>
                    </select>
                </div>
                {/* Y-AXIS OPTIONS */}
                <div className="stats-option-box">
                    <h4>Vertical axis</h4>
                    <select className="stats-select" id="select-v-ax" onChange={() => {
                        const newPref = getPreferencesChange();
                        props.prefChange(newPref);
                    }}>
                        <option value="ppm">Points/Minutes</option>
                        <option value="ppp">Points/Price</option>
                        <option value="ppppm">Points/Price/Minutes</option>
                    </select>
                </div>
                {/* POSITIONS OPTIONS */}
                <div className="stats-option-box" id="statsboard-options-positions">
                    <h4>Select positions</h4>
                        <div id="stats-checkbox-container">
                            <label htmlFor="GKP">GKP</label>
                            <input type="checkbox" name="GKP" id="GKP-CB" defaultChecked="checked" onChange={() => {
                                const newPref = getPreferencesChange();
                                props.prefChange(newPref);
                             }} 
                            ></input>
                            <label htmlFor="DEF">DEF</label>
                            <input type="checkbox" name="DEF" id="DEF-CB" defaultChecked="checked" onChange={() => {
                                const newPref = getPreferencesChange();
                                props.prefChange(newPref);
                             }} 
                            ></input>
                            <label htmlFor="MID">MID</label>
                            <input type="checkbox" name="MID" id="MID-CB" defaultChecked="checked" onChange={() => {
                                const newPref = getPreferencesChange();
                                props.prefChange(newPref);
                             }} 
                            ></input>
                            <label htmlFor="FWD">FWD</label>
                            <input type="checkbox" name="FWD" id="FWD-CB" defaultChecked="checked" onChange={() => {
                                const newPref = getPreferencesChange();
                                props.prefChange(newPref);
                             }} 
                            ></input>
                        </div>
                </div>
                {/* MINUTES OPTIONS */}
                <div className="stats-option-box" id="statsboard-options-minutes">
                   <h4>Min. minutes played</h4>
                   <div id="minutes-container">
                       <input type="range" id="minutes-slider" onInput={slideChange} min="0" max="2000"></input>
                       <p id="minutes-display">1000</p>
                   </div>
                </div>
                {/* MAX PRICE */}
                <div className="stats-option-box" id="statsboard-options-xaxis" >
                    <h4>Max. price</h4>
                    <input type="number" id="max-price" min="3.5" max="14.0" step="0.1" placeholder="14.0"></input>
                </div>
                {/* TEAMS OPTIONS */}
                <div className="stats-option-box" id="statsboard-options-teams">
                    <h4>Select team</h4>
                    <select className="stats-select" id="select-team">
                        <option>All teams</option>
                        <option>Arsenal</option>
                        <option>Aston Villa</option>
                        <option>Chelsea</option>
                        <option>Manchester United</option>
                        <option>Manchester City</option>
                    </select>
                </div>
                {/* SHOW NAMES */}
                <div className="stats-option-box" id="statsboard-options-teams">
                    <h4>Show names</h4>
                </div>
                {/* SOMETHING ELSE */}
                <div className="stats-option-box" id="statsboard-options-teams">
                    <h4>Somethin else</h4>
                </div>   
            </div>
    )
}

function getPreferencesChange() {
    const hAxisInput = document.getElementById('select-h-ax').value;
    const vAxisInput = document.getElementById('select-v-ax').value;

    const isCheckedGKP = document.getElementById('GKP-CB').checked;
    const isCheckedDEF = document.getElementById('DEF-CB').checked;
    const isCheckedMID = document.getElementById('MID-CB').checked;
    const isCheckedFWD = document.getElementById('FWD-CB').checked;

    return [hAxisInput, vAxisInput, isCheckedGKP, isCheckedDEF, isCheckedMID, isCheckedFWD]

}

function slideChange() {
    var slider = document.getElementById("minutes-slider");
    var output = document.getElementById("minutes-display");
    output.innerHTML = slider.value; // Display the default slider value

    // Update the current slider value (each time you drag the slider handle)
    slider.oninput = function() {
    output.innerHTML = this.value;
    }
}

export default OptionsContainer;
