import { useEffect, useState } from 'react';
import setTheme from '../../utils/Themes';

function CaptainPicks( props ) {

    //set theme to purple when rendering the players page
    useEffect(() => {
        setTheme('#38003c', '#530553');
    })

    useEffect(() => {
        props.handlePageSwitch('Captain Picks');
    })

    const [ selectedChoice, setSelectedChoice ] = useState('SIMPLE');

    const handleChoiceSelection = (newSelection) => {
        setSelectedChoice(newSelection);
    }

    return (
        <div id="captain-picks-main">
            <ChoiceContainer 
                handleChoiceSelection={handleChoiceSelection} 
                selectedChoice={selectedChoice}
            />
            <div id="captain-picks-container">

            </div>
        </div>
    )
}

function ChoiceContainer( props ) {
    return (
        <div id="captain-picks-choice-container">
            <Choice text={'SIMPLE'} handleChoiceSelection={props.handleChoiceSelection} selectedChoice={props.selectedChoice}/>
            <Choice text={'ADVANCED'} handleChoiceSelection={props.handleChoiceSelection} selectedChoice={props.selectedChoice}/>
        </div>
    )
}

function Choice( props ) {

    const handleOnClick = () => {
        props.handleChoiceSelection(props.text);
        setColor(getColor());
    }

    const getColor = () => {
        if (props.text === 'SIMPLE') {
            return props.selectedChoice === 'SIMPLE' ? 'purple' : '#38003c';
        }

        else {
            return props.selectedChoice === 'ADVANCED' ? 'purple' : '#38003c';
        }
    }


    const [ color, setColor ] = useState(getColor())

    return (
        <div style={{ backgroundColor: color }} className="captain-picks-choice" onClick={handleOnClick}>
            <h3>{props.text}</h3>
        </div>
    )
}

export default CaptainPicks;
